import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const About = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState({});

  const callAboutPage = async () => {
    try {
      const res = await fetch('/about', {
        method: "GET",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
          // Include the authentication token or cookie here
          Authorization: "Bearer YOUR_AUTH_TOKEN",
        },
        credentials: "include",
      });

      console.log(res);

      const data = await res.json();
      console.log(data);

      if (!data.status === 200 || !res) {
        const error = new Error(data.error);
        throw error;
      }

    } catch (err) {
      navigate("/Login");
    }
  }

  useEffect(() => {
    // Check if the user is authenticated before making the API call
    const isAuthenticated = true; // Replace this with your authentication logic

    if (isAuthenticated) {
      callAboutPage();
    } else {
      navigate("/Login");
    }
  }, []);

  return (
    <>
      <h1>Pooja Sorathiya</h1>
    </>
  )
}

export default About;
